    document.body.appendChild(link);
    link.click();
  };
  return (
    <div style={{ display: "flex", flexDirection: "row" }}>
      {data.map((e) => (
        <div class="card" style={{ margin: 30 }}>
          <div style={{ display: "flex" }}>
            <img
              class="card-img-top"
