import React, { Component } from "react";
import { AnchorButton } from "@blueprintjs/core";
import "../style.scss";
import axios from "axios";

export default class Upload extends Component {
  constructor(props) {
    super(props);
    this.state = {
      file: null,
      // image:"",
      title: "",
      description: "",
      filename: "",
      //   alert: false,
    };
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  handleChangeimage = (e) => {
    //Get current selected or dropped file (it gives you the ability to load multiple images).
    this.setState({
      image: e.currentTarget.files[0],
    });
    // console.log(this.state.image)
    const image = e.currentTarget.files[0];
    console.log("imaaaage", image);

    if (image) {
      //Create instance
      let fileReader = new FileReader();
      //Register event listeners

      //Read the file as a Data URL (which gonna give you a base64 encoded image data)
      fileReader.readAsDataURL(image);
    }
  };

  onFileLoad = (e) => {
    //Get current selected or dropped file (it gives you the ability to load multiple images).
    this.setState({
      file: e.currentTarget.files[0],
    });
    console.log(this.state.file);
    const file = e.currentTarget.files[0];
    console.log("zzzzzzzzzzzzzzz", file.name);

    if (file) {
      //Create instance
      let fileReader = new FileReader();
      //Register event listeners
      fileReader.onload = () => {
        // console.log("IMAGE LOADED: ", fileReader.result);
      };

      //Read the file as a Data URL (which gonna give you a base64 encoded image data)
      fileReader.readAsDataURL(file);
    }
  };
  fileUpload = () => {
    // this.setState({
    //   alert: !this.state.alert,
    // });

    console.log(this.state.image);

    // const fd = new FormData();
