          >
            <strong>PDF UPLODED</strong> You should check your session to see it
            <button
              type="button"
              class="close"
              data-dismiss="alert"
              aria-label="Close"
            >
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
        ) : null} */}
        <div className="dragandform">
          <div
            className="inner-container"
            style={{
              display: "flex",
              flexDirection: "column",
            }}
          >
            {/* <div className="sub-header">Drag a Book in here</div> */}
            <div className="draggable-container">
              <input
                type="file"
                id="file-browser-input"
                name="file-browser-input"
                onChange={this.onFileLoad}
              />
              <div className="files-preview-container"></div>
              <div className="helper-text">
                {this.state.file === null ? (
                  <h3>Drag and Drop Books Here</h3>
                ) : (
                  <div style={{ textAlign: "center" }}>
                    <h3>{this.state.file.name}</h3>{" "}
                    <h3>{this.state.file.size} Ko</h3>
                  </div>
                )}
              </div>
              <div className="file-browser-container">
                <AnchorButton
                  text="Upload"
                  onClick={this.fileUpload}
                  style={{ marginBottom: 30, backgroundColor: "#00AAEE" }}
                />
              </div>
            </div>
            {/* <AnchorButton text="Upload" onClick={this.fileUpload} /> */}
          </div>

          {/* <form style={{padding:100}}> */}
          <form
