import React, { useEffect } from "react";
import { Button, Form } from "react-bootstrap";
import { useState } from "react";
import axios from "axios";

const Test = () => {
  const [data, setData] = useState([]);
  const [name, setname] = useState("");
  const [lastName, setlastName] = useState("");
  const [email, setemail] = useState("");
  const [text, setText] = useState("");
  useEffect(() => {
    const fetchData = async () => {
      const userId = localStorage.getItem("id");
      const result = await axios(`http://localhost:2000/api/auth/${userId}`);
      console.log(result.data, "useerrr");
      setData(result.data);
      setname(data.name);
      setlastName(data.lastName);
      setemail(data.email);
    };

    fetchData();
  });

  return (
    <>
      <section id="cover" class="min-vh-100">
        <div id="cover-caption" style={{ marginTop: "100px" }}>
          <div class="container">
            <div class="row text-white">
              <div class="col-xl-5 col-lg-6 col-md-8 col-sm-10 mx-auto text-center form p-4">
                <div class="px-2">
                  <Form>
                    <Form.Group controlId="formBasicPassword">
                      <Form.Label className="itemlabel">text</Form.Label>
                      {/* <Form.Control */}
                      <textarea
                        type="text"
                        name="text"
                        value={text}
                        className="itemStyle"
                        onChange={(e) => setText(e.target.value)}
                      ></textarea>
                    </Form.Group>
                    <div
                      style={{
                        justifyContent: "space-between",
                        padding: "5% 14%",
                      }}
                    >
                      <Button
                        className="buttonStyle"
                        variant="primary"
                        type="submit"
                        style={{ backgroundColor: "#00AAEE" }}
                        onClick={() =>
                          //  UpdateUser()
                          {
                            const editUser = {
                              name: name,
                              lastName: lastName,
                              email: email,
                              text,
                            };

                            console.log(editUser, "editUser");
                            const userId = localStorage.getItem("id");
                            axios.put(
                              `http://localhost:2000/api/auth/${userId}/contact`,
                              editUser
                            );
                            alert("mesg sent with success");
                          }
                        }
                      >
                        Submit
                      </Button>
                    </div>
                  </Form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Test;
