  const usersRef = firebase.database().ref("users");

  const connectedRef = firebase.database().ref(".info/connected");

  const statusRef = firebase.database().ref("status");
  const [name, setname] = useState("");
  const [lastName, setlastName] = useState("");
  const [email, setemail] = useState("");
  useEffect(() => {
    usersRef.on("child_added", (snap) => {
      setUsersState((currentState) => {
        let updatedState = [...currentState];

        let user = snap.val();
        user.name = user.displayName;
        user.email = user.email;
        user.status = user.status;
        user.id = snap.key;
        user.isPrivateChat = true;
        updatedState.push(user);

        return updatedState;
      });
    });

    connectedRef.on("value", (snap) => {
      if (props.user && snap.val()) {
        const userStatusRef = statusRef.child(props.user.uid);
        userStatusRef.set(true);
        userStatusRef.onDisconnect().remove();
      }
    });

    return () => {
      usersRef.off();
      connectedRef.off();
    };
  }, [props.user]);

  useEffect(() => {
    statusRef.on("child_added", (snap) => {
      setConnectedUsersState((currentState) => {
        let updatedState = [...currentState];
        updatedState.push(snap.key);
        return updatedState;
      });
    });

    statusRef.on("child_removed", (snap) => {
      setConnectedUsersState((currentState) => {
        let updatedState = [...currentState];

        let index = updatedState.indexOf(snap.key);
        updatedState.splice(index, 1);
        return updatedState;
      });
    });

    return () => statusRef.off();
  }, [usersState]);

  const displayUser = () => {
    if (usersState.length > 0) {
      return usersState
        .filter((user) => user.id === store.getState().user.currentUser.uid)
        .map((user) => {
          return (
            <>
              <section id="cover" class="min-vh-100">
                <div id="cover-caption" style={{ marginTop: "100px" }}>
                  <div class="container">
                    <div class="row text-white">
                      <div class="col-xl-5 col-lg-6 col-md-8 col-sm-10 mx-auto text-center form p-4">
                        <div class="px-2">
                          <Form>
                            <Form.Group controlId="formBasicEmail">
                              <Form.Label className="itemlabel">
                                Name
                              </Form.Label>
                              <Form.Control
                                className="itemStyle"
                                type="name"
                                placeholder={props.user.displayName}
                                name="name"
                                value={name}
                                onChange={(e) => setname(e.target.value)}
                              />
                            </Form.Group>

                            <Form.Group controlId="formBasicPassword">
                              <Form.Label className="itemlabel">
                                lastName
                              </Form.Label>
                              <Form.Control
                                type="lastName"
                                placeholder={props.user.displayName}
                                name="lastName"
                                value={lastName}
                                className="itemStyle"
                                onChange={(e) => setlastName(e.target.value)}
                              />
                            </Form.Group>

                            <Form.Group controlId="formBasicPassword">
                              <Form.Label className="itemlabel">
                                Email
                              </Form.Label>
                              <Form.Control
                                type="email"
                                placeholder={props.user.email}
                                name="email"
                                value={email}
                                className="itemStyle"
                                onChange={(e) => setemail(e.target.value)}
                              />
                            </Form.Group>
                            <div
                              style={{
                                justifyContent: "space-between",
                                padding: "5% 14%",
                              }}
                            >
                              {console.log(props.user.uid, "ttfdbn")}
                              <Button
                                className="buttonStyle"
                                variant="primary"
                                type="submit"
                                style={{ backgroundColor: "#00AAEE" }}
                                onClick={() => {
                                  const id =
                                    store.getState().user.currentUser.uid;
                                  const accountStatus = usersRef.child(id);

                                  accountStatus.update({
                                    email: "test55@gmail.com",
                                    displayName: "test55",
                                  });
                                  alert("edit user profile with success");
                                }}
                              >
                                Submit
                              </Button>
                            </div>
                          </Form>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </section>
            </>
          );
        });
    }
  };

  return <div>{displayUser()}</div>;
};

const mapStateToProps = (state) => {
  return {
    user: state.user.currentUser,
    channel: state.channel.currentChannel,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    // selectChannel: (channel) => dispatch(setChannel(channel)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(EditProfile);
