import React, { useState } from "react";
// import { Grid, Header, Icon, Image, Dropdown } from "semantic-ui-react";
import { connect } from "react-redux";
// import firebase from "../../../server/firebase";
import { Button, Form } from "react-bootstrap";
// import "./UserInfo.css";
import firebase from "../../server/firebase";
const UserInfo = (props) => {
  const usersRef = firebase.database().ref("users");
  const [name, setname] = useState("");
  const [lastName, setlastName] = useState("");
  const [email, setemail] = useState("");
  if (props.user) {
    return (
      // <Grid>
      //     <Grid.Column>
      //         <Grid.Row className="userinfo_grid_row">
      //             <Header inverted as="h2">
      //                 {/* <Icon name="slack" /> */}
      //                 <Header.Content>Soft Community</Header.Content>
      //             </Header>
      //             <Header className="userinfo_displayname" inverted as="h4">
      //                 <Dropdown
      //                     trigger={
      //                         <span>

      //                             {props.user.displayName}
      //                         </span>
      //                     }
      //                     options={getDropDownOptions()}
      //                 >
      //                 </Dropdown>

      //             </Header>
      //         </Grid.Row>
      //     </Grid.Column>
      // </Grid>

      <>
        <section id="cover" class="min-vh-100">
          <div id="cover-caption" style={{ marginTop: "100px" }}>
            <div class="container">
              <div class="row text-white">
                <div class="col-xl-5 col-lg-6 col-md-8 col-sm-10 mx-auto text-center form p-4">
                  <div class="px-2">
                    <Form>
                      <Form.Group controlId="formBasicEmail">
                        <Form.Label className="itemlabel">Name</Form.Label>
                        <Form.Control
                          className="itemStyle"
                          type="name"
                          placeholder={props.user.displayName}
                          name="name"
                          value={name}
                          onChange={(e) => setname(e.target.value)}
                        />
                      </Form.Group>

                      <Form.Group controlId="formBasicPassword">
                        <Form.Label className="itemlabel">lastName</Form.Label>
                        <Form.Control
                          type="lastName"
                          placeholder={props.user.displayName}
                          name="lastName"
                          value={lastName}
                          className="itemStyle"
                          onChange={(e) => setlastName(e.target.value)}
                        />
                      </Form.Group>

                      <Form.Group controlId="formBasicPassword">
                        <Form.Label className="itemlabel">Email</Form.Label>
                        <Form.Control
                          type="email"
                          placeholder={props.user.email}
                          name="email"
                          value={email}
                          className="itemStyle"
                          onChange={(e) => setemail(e.target.value)}
                        />
                      </Form.Group>
                      <div
                        style={{
                          justifyContent: "space-between",
                          padding: "5% 14%",
                        }}
                      >
                        {console.log(props.user.uid, "ttfdbn")}
                        {props.user.map((e) => (
                          <Button
                            className="buttonStyle"
                            variant="primary"
                            type="submit"
                            style={{ backgroundColor: "#00AAEE" }}
                            onClick={(e) =>
                              //  UpdateUser()
                              {
                                // console.log(editUser, "editUser");
                                // const userId = localStorage.getItem("id");
                                // axios.put(
                                //   `http://localhost:2000/api/auth/${userId}`,
                                //   editUser
                                // );
                                {
                                  console.log(e.uid, "ttfdbn");
                                }
                                const accountStatus = usersRef.child(e.uid);

                                accountStatus.update({
                                  email: email,
                                  displayName: name,
                                });
                                alert("edit user profile with success");
                              }
                            }
                          >
                            Submit
                          </Button>
                        ))}
                      </div>
                    </Form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </>
    );
  }
  return null;
};

const mapStateToProps = (state) => {
  return {
    user: state.user.currentUser,
  };
};

export default connect(mapStateToProps)(UserInfo);
