import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
// import firebase from "../../server/firebase";
import firebase from "../../server/firebase";

import { Menu } from 'semantic-ui-react';
import UserInfo from "./UserInfo/UserInfo.component";
import Channels from "./Channels/Channels.component";
import PrivateChat from "./PrivateChat/PrivateChat.component";
import FavouriteChannels from "./FavouriteChannels/FavouriteChannels.component";
import { Link, useHistory } from "react-router-dom";
// import { Nav, Navbar, Container, Button } from "react-bootstrap";
import "./SideBar.css";

export const SideBar = (props) => {
  if (props.user) {
    return (<Grid>
        <Grid.Column>
            <Grid.Row className="userinfo_grid_row">
                <Header inverted as="h2">
                    {/* <Icon name="slack" /> */}
                    <Header.Content>Soft Community</Header.Content>
                </Header>
                <Header className="userinfo_displayname" inverted as="h4">
                    <Dropdown
                        trigger={
                            <span>
                                <Image src={props.user.photoURL} avatar></Image>
                                {props.user.displayName}
                            </span>
                        }
                        options={getDropDownOptions()}
                    >
                    </Dropdown>

                </Header>
            </Grid.Row>
        </Grid.Column>
    </Grid>)
}
return null;
}

const mapStateToProps = (state) => {
return {
    user: state.user.currentUser
}
}

connect(mapStateToProps)(SideBar);
