import React, { Component } from "react";
import { AnchorButton } from "@blueprintjs/core";
import "../style.scss";
import axios from "axios";

export default class Upload extends Component {
  constructor(props) {
    super(props);
    this.state = {
      file: null,
      // image:"",
      title: "",
      description: "",
      filename: "",
      alert: false,
    };
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  onFileLoad = (e) => {
    //Get current selected or dropped file (it gives you the ability to load multiple images).
    this.setState({
      file: e.currentTarget.files[0],
    });
    console.log(this.state.file);
    const file = e.currentTarget.files[0];
    console.log("zzzzzzzzzzzzzzz", file.name);

    if (file) {
      //Create instance
      let fileReader = new FileReader();
      //Register event listeners
      fileReader.onload = () => {
        // console.log("IMAGE LOADED: ", fileReader.result);
      };

      //Read the file as a Data URL (which gonna give you a base64 encoded image data)
      fileReader.readAsDataURL(file);
    }
  };
  fileUpload = () => {
    const data = new FormData();
    data.append("file", this.state.file);
    axios.post("http://localhost:2000/upload", data);
    // this.setState({ alert: !this.state.alert });
    setTimeout(() => this.setState({ alert: !this.state.alert }), 3000);
  };

  render() {
    return (
      <div>
        {this.state.alert ? (
          <div
            class="alert alert-warning alert-dismissible fade show"
            role="alert"
          >
            <strong>PDF UPLODED</strong>
            <button
              type="button"
              class="close"
              data-dismiss="alert"
              aria-label="Close"
            >
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
        ) : null}
        <div className="dragandform">
          <div
            className="inner-container"
            style={{
              display: "flex",
              flexDirection: "column",
            }}
          >
            {/* <div className="sub-header">Drag a Book in here</div> */}
            <div className="draggable-container">
              <input
                type="file"
                id="file-browser-input"
                name="file-browser-input"
                onChange={this.onFileLoad}
              />
              <div className="files-preview-container"></div>
              <div className="helper-text">
                {this.state.file === null ? (
                  <h3>Drag and Drop Books Here</h3>
                ) : (
                  <div style={{ textAlign: "center" }}>
                    <h3>{this.state.file.name}</h3>{" "}
                    <h3>{this.state.file.size} Ko</h3>
                  </div>
                )}
              </div>
              <div className="file-browser-container">
                <AnchorButton
                  text="Upload"
                  onClick={this.fileUpload}
                  style={{
                    marginBottom: 30,
                    backgroundColor: "#00AAEE",
                    color: "grey",
                    fontWeight: "bold",
                  }}
                />
              </div>
            </div>
            {/* <AnchorButton text="Upload" onClick={this.fileUpload} /> */}
          </div>

          {/* <form style={{padding:100}}> */}
          <form
            style={{
              padding: "100px",
              display: "flex",
              flexDirection: "column",
            }}
          >
            {/* <input
              type="title"
              id="defaultContactFormEmail"
              class="form-control mb-4"
              name="title"
              placeholder="title"
              onChange={this.handleChange}
              style={{ padding: "10px", margin: "10px" }}
              required
            />



            <div class="form-group" style={{ padding: "10px", margin: "10px" }}>
              <input
                type="file"
                class="form-control-file"
                id="exampleFormControlFile1"
                name="image"
                onChange={this.handleChangeimage}
                required
              />
            </div>

            <textarea
              id="textarea"
              class="form-control mb-4"
              placeholder="description"
              name="description"
              onChange={this.handleChange}
              style={{ padding: "10px", margin: "10px" }}
              required
            ></textarea> */}

            {/* <AnchorButton
              type="button"
              text="Upload"
              onClick={this.fileUpload}
              style={{ height: 20, width: 60 }}
            /> */}
          </form>
        </div>
      </div>
    );
  }
}
