  <div style={{display:"flex", flexDirection:"column"}}>
  <Menu.Item style={{ fontSize: '17px' }}>
             <Link
                style={{ color: "white", textDecoration: "none" }}
                to="/calendar"
              >
               <Icon name="mail" />   Calendar
