        <Menu.Item style={{ fontSize: '17px' }}>
             <Link
                style={{ color: "white", textDecoration: "none" }}
                to="/listcontact"
              >
               <Icon name="mail" />    List Contact
              </Link>

        </Menu.Item>
        <Menu.Item style={{ fontSize: '17px' }}>
             <Link
                style={{ color: "white", textDecoration: "none" }}
                to="/upload"
              >
               <Icon name="mail" />      Upload
              </Link>

