             <Link
                style={{ color: "white", textDecoration: "none" }}
                to="/shared"
              >
               <Icon name="mail" />          Shared Document
              </Link>

        </Menu.Item>
        <Menu.Item style={{ fontSize: '17px' }}>
             <Link
                style={{ color: "white", textDecoration: "none" }}
                to="/edit"
              >
               <Icon name="mail" />           Edit Profile
              </Link>

        </Menu.Item>

        { user.account === "admin" && 
           <Menu.Item style={{ fontSize: '17px' }}>
           <Link
              style={{ color: "white", textDecoration: "none" }}
              to="/ApprouveUser"
            >
             <Icon name="mail" />          ApprouveUser
            </Link>

      </Menu.Item>
              } 
