  const [data, setData] = useState([]);
  const [searchedName, setSearchedName] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios("http://localhost:2000/api/auth/all");

      setData(result.data);
    };

    fetchData();
  }, []);

  // searching for items
  searcheditem = (search) => {
    setSearchedName(search);
    console.log(searchedName);
  };
  return (
    <>
      <Search searcheditem={searcheditem()} />
      <div>
        <table className="table">
          <tbody className="table">
            {data.map((user) => (
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-around",
                  alignSelf: "stretch",
                }}
              >
                <a>{user.name}</a>
                <a>{user.email}</a>

                {/* <button
                type="button"
                class="btn btn-link"
